import { Component } from "react";
import "./Styles/styles.css";

class Main extends Component{

    constructor(){
        super()
        this.getAllContacts = this.getAllContacts.bind(this);
        this.state = {
            contacts: []
        }
    }

    getAllContacts(){
        // axios.get('http://localhost:8080/contacts').then(response => {
        //     let elementsArr = [];
        //     response.data.forEach((contactObj, index) => {
        //         elementsArr.push(
        //             <div key = { index }>
        //                 {contactObj.name}
        //             </div>
        //         );
        //     });
        //     this.setState({contacts: elementsArr})});

        // Make an AJAX request using the XMLHttpRequest object
        let request = new XMLHttpRequest();

        // Set the request URL and method
        request.open('GET', 'http://localhost:8080/contacts', true);

        // Define the callback function
        request.onload = function() {
        // Check the response status code
        if (request.status === 200) {
            // Parse the JSON response
            let data = JSON.parse(request.responseText);

            // Do something with the data
            let elementsArr = [];
            data.forEach((contactObj, index) => {
                elementsArr.push(
                    <div key = { index }>
                        { contactObj.name }
                    </div>
                );
            });
            this.setState({contacts: elementsArr});
        }
        };

        // Send the request
        request.send();

    }

    componentDidMount() {
        this.getAllContacts();
    }

    render(){
        return <div>
                { this.state.contacts }
            </div>
    }

}

export default Main;
