const myAjax = {
    AjaxGetAllContacts: () => {
      return new Promise((resolve, reject) => {
        let request = new XMLHttpRequest();
        request.open('GET', 'http://localhost:8080/contacts', true);

        request.onload = () => {
          if (request.status === 200)
            resolve(JSON.parse(request.responseText));
          else
            reject(new Error(`Request failed with status ${request.status}`));
        };

        request.onerror = () => {
          reject(new Error('Request failed'));
        };
        request.send();
      });
    },

    
    AjaxAddContact: (contactObj) => {
      return new Promise((resolve, reject) => {
      var request = new XMLHttpRequest();
      request.open("POST", 'http://localhost:8080/addContact', true);
      request.setRequestHeader("Content-Type", "application/json");

      request.onload = () => {
        if (request.status === 200 || request.status === 201)
          resolve(JSON.parse(request.responseText));
        else
          reject(new Error(`Request failed with status ${request.status}`));
      };

      request.onerror = () => {
        reject(new Error('Request failed'));
      };
      request.send(JSON.stringify(contactObj));
    });  
  },


    AjaxModifyContact: (contactObj) => {
      return new Promise((resolve, reject) => {
      var request = new XMLHttpRequest();
      request.open("PUT", `http://localhost:8080/modifyContact/${contactObj.idToModify}`, true);
      request.setRequestHeader("Content-Type", "application/json");

      request.onload = () => {
        if (request.status === 200 || request.status === 201)
          resolve(JSON.parse(request.responseText));
        else
          reject(new Error(`Request failed with status ${request.status}`));
      };

      request.onerror = () => {
        reject(new Error('Request failed'));
      };
      request.send(JSON.stringify(contactObj));
    });  
  },

}
  
  export default myAjax;
