import { Component } from "react";
import "./Styles/styles.css";
import ContactCard from "./Components/ContactCard";

class Main extends Component{

    constructor(){
        super()
        this.getAllContacts = this.getAllContacts.bind(this);
        this.state = {
            contacts: []
        }
    }

    getAllContacts(){
        let request = new XMLHttpRequest();
        request.open('GET', 'http://localhost:8080/contacts', true);
        request.onload = () => {
        if (request.status === 200) {
            let data = JSON.parse(request.responseText);
            let elementsArr = [];
            data.forEach((contactObj, index) => {
                elementsArr.push(<ContactCard key = { index } name = { contactObj.name } />);
            });
            this.setState({contacts: elementsArr});
            }
        };
        request.send();
    }

    componentDidMount() {
        this.getAllContacts();
    }

    render(){
        return <div id="allCards">
                { this.state.contacts }
            </div>
    }

}

export default Main;
