        return (
            <div>
                <h1 id="addHeadline"> Add a new contact </h1>
                <form className="addForm" onSubmit={ () => this.addContact }>
                    <input id="input1" type="text" placeholder="New contact's name"/>
                    <input id="input2" type="text" placeholder="New contact's role"/>
                    <input id="input3" type="text" placeholder="New contact's location"/>
                    <input id="input4" type="text" placeholder="New contact's company"/>
                    <input id="input5" type="text" placeholder="New contact's address"/>
                    <input id="input6" type="text" placeholder="New contact's state"/>
                    <input id="input7" type="text" placeholder="New contact's phone"/>
                    <button id="addButton" type="submit">Send</button>
                </form>
            </div>);
    }

}

export default ContactForm;
