import { Component } from "react";
import axios from "axios"
import "./Stylesss"

class Main extends Component{

    constructor(){
        super()
        this.operatorChanged = this.operatorChanged.bind(this);
        this.calcaulate = this.calcaulate.bind(this);
        this.state = {
            operator: '+'
        }
    }

    operatorChanged(event){
        this.setState({operator: event.target.value});
    }

    async calcaulate(event){
        event.preventDefault();
        const obj = {
            operand1: event.target.elements.num1.value,
            operator: this.state.operator,
            operand2: event.target.elements.num2.value
        };

          await axios.post('http://localhost:8080/calculate', {obj: obj}).then(response => {
           event.target.elements.result.value = response.data});
    }

    render(){
        return <form onSubmit={this.calcaulate}>

                <input name="num1" type="number" placeholder="Enter numeric value" required={true}/>

                <select value={this.state.operator} onChange={this.operatorChanged}>
                    <option value="+"> + </option>
                    <option value="-"> - </option>
                    <option value="*"> * </option>
                    <option value="/"> / </option>
                    <option value="^"> ^ </option>
                </select>

                <input name="num2" type="number" placeholder="Enter numeric value" required={true}/>

                <label>  =  </label>

                <input name="result" type="text"/>

                <button type="submit"> Count </button>


            </form>
    }

}

export default Main;
